Module.Content:


#requireResource("jira.webresources:almond")
        #requireResource("com.atlassian.jira.gadgets:autocomplete")
        #requireResource("com.atlassian.jira.gadgets:dashboard-item-common-resources")
#includeResources()

<script type="text/javascript" charset="utf-8">

    AJS.Gadget({
        baseUrl: "__ATLASSIAN_BASE_URL__",
        useOauth: "/rest/gadget/1.0/currentUser",
        config: {
            descriptor: function () {
                var gadget = this;
                var searchParam;
                if (/^jql-/.test(gadget.getPref('projectOrFilterId')) || gadget.getPref('isPopup') === "true") {
                    searchParam = {
                        userpref: "projectOrFilterId",
                        type: "hidden",
                        value: gadgets.util.unescapeString(gadget.getPref("projectOrFilterId"))
                    };
                } else {
                    searchParam = AJS.gadget.fields.projectOrFilterPicker(gadget, "projectOrFilterId");
                }
                return {
                    action: "/rest/dianrong-gadget/1.0/planned-vs-released-chart/validate",
                    theme: function () {
                        if (gadgets.window.getViewportDimensions().width < 450) {
                            return "gdt top-label";
                        } else {
                            return "gdt";
                        }
                    }(),
                    fields: [searchParam, {
                        userpref: "period",
                        label: gadget.getMsg("gadget.planned.vs.released.chart.period.label"),
                        description: gadget.getMsg("gadget.planned.vs.released.chart.period.description"),
                        type: "select",
                        selected: gadget.getPref('period'),
                        options: [{
                            label: "Released Version",
                            value: "releasedVersion"
                        }]
                    }, {
                        userpref: "previous",
                        label: gadget.getMsg("gadget.planned.vs.released.chart.previous.label"),
                        description: gadget.getMsg("gadget.planned.vs.released.chart.previous.description"),
                        type: "text",
                        value: gadget.getPref('previous')
                    }, AJS.gadget.fields.nowConfigured()]
                };
            }
        },
        view: {
            enableReload: true, onResizeReload: true, onResizeAdjustHeight: true, template: function (args) {
                var gadget = this;
                gadget.getView().addClass("chart").empty();
                gadget.getView().addClass("g-created-resolved");
                var requiresTitle = function () {
                    return args.chart.filterTitle && args.chart.filterTitle !== "" && args.chart.filterTitle !== "gadget.common.anonymous.filter" && preferences.isPopup === "false";
                };
                var setTitles = function () {
                    if (requiresTitle()) { // This is used to pass the name of filter or project to the picker
                        gadget.projectOrFilterName = args.chart.filterTitle;
                        gadgets.window.setTitle(AJS.format("__MSG_gadget.created.vs.resolved.title.specific__", args.chart.filterTitle));
                    }
                }();
                var getChartContainer = function () {
                    var chart = AJS.$("<div id='chart' />").appendTo(gadget.getView());
                    return function () {
                        return chart;
                    };
                }();
                var safeEscapeString = function (text) {
                    if (text) {
                        return gadgets.util.escapeString(text);
                    } else {
                        return '';
                    }
                };
                var getChartImg = function () {
                    AJS.$("#chart", gadget.getView()).get(0).innerHTML += "<img style='display:none' src='" + args.chart.base64Image + "' alt='" + safeEscapeString(args.chart.filterTitle) + "' usemap='#" + args.chart.imageMapName + "' height='" + args.chart.height + "' width='" + args.chart.width + "' />";
                    gadget.getView().append(args.chart.imageMap);
                    gadget.showLoading();
                    var chartImg = AJS.$("img", getChartContainer());
                    AJS.$(chartImg, gadget.getView()).load(function () {
                        AJS.$(this).show();
                        gadget.hideLoading();
                        gadget.resize();
                    });
                    return function () {
                        return chartImg;
                    };
                }();
                var setSummary = function () {
                    var linkStart = "";
                    var linkEnd = "";
                    if (requiresTitle()) {
                        linkStart = "<a href=\"https://issue.dianrong.com" + args.chart.filterUrl + "\" target=\"_parent\" title=\"" + safeEscapeString(args.chart.filterTitle) + "\">";
                        linkEnd = "</a>";
                    }
                    getChartContainer().append(AJS.$("<p/>").append(AJS.format("__MSG_gadget.created.vs.resolved.issues__", linkStart, linkEnd, "<span class=\"created\"><strong>", args.chart.issuesCreated, "</strong></span>", "<span class=\"resolved\"><strong>", args.chart.issuesResolved, "</strong></span>"))).append(AJS.$("<p/>").append(AJS.format("__MSG_gadget.common.chart.period__", "<span><strong>", gadget.getPref("daysprevious"), "</strong></span>", "<span><strong>", gadget.getMsg("gadget.common.period." + gadget.getPref("periodName")), "</strong></span>")));
                }();
                if (gadgets.views.getCurrentView().getName() === "canvas") {
                    var dataTable = AJS.gadgets.templater.Table({
                        descriptor: function (args) {
                            if (gadget.getPref("showUnresolvedTrend") === "true") {
                                return {
                                    cols: [{
                                        header: gadget.getMsg("gadget.common.period.name"),
                                        preSorted: true,
                                        sort: "asc"
                                    }, {header: gadget.getMsg("gadget.created.vs.resolved.created")}, {header: gadget.getMsg("gadget.created.vs.resolved.resolved")}, {header: gadget.getMsg("gadget.created.vs.resolved.trend")}],
                                    data: function () {
                                        var rows = [];
                                        AJS.$(args.chartData).each(function (i) {
                                            var resolvedLabel = "";
                                            var createdLabel = "";
                                            if (gadget.getPref("isCumulative") == "false") {
                                                resolvedLabel = "<a href='" + this.resolvedUrl + "'>" + this.resolvedValue + "</a>";
                                                createdLabel = "<a href='" + this.createdUrl + "'>" + this.createdValue + "</a>";
                                            } else {
                                                resolvedLabel = this.resolvedValue;
                                                createdLabel = this.createdValue;
                                            }
                                            rows.push([{value: i, label: this.key}, {
                                                value: this.createdValue,
                                                label: createdLabel
                                            }, {value: this.resolvedValue, label: resolvedLabel}, {
                                                value: this.trendCount,
                                                label: this.trendCount
                                            }]);
                                        });
                                        return rows;
                                    }()
                                };
                            } else {
                                return {
                                    cols: [{
                                        header: gadget.getMsg("gadget.common.period.name"),
                                        preSorted: true,
                                        sort: "asc"
                                    }, {header: gadget.getMsg("gadget.created.vs.resolved.created")}, {header: gadget.getMsg("gadget.created.vs.resolved.resolved")}],
                                    data: function () {
                                        var rows = [];
                                        AJS.$(args.chartData).each(function (i) {
                                            var resolvedLabel = "";
                                            var createdLabel = "";
                                            if (gadget.getPref("isCumulative") == "false") {
                                                resolvedLabel = "<a href='" + this.resolvedUrl + "'>" + this.resolvedValue + "</a>";
                                                createdLabel = "<a href='" + this.createdUrl + "'>" + this.createdValue + "</a>";
                                            } else {
                                                resolvedLabel = this.resolvedValue;
                                                createdLabel = this.createdValue;
                                            }
                                            rows.push([{value: i, label: this.key}, {
                                                value: this.createdValue,
                                                label: createdLabel
                                            }, {value: this.resolvedValue, label: resolvedLabel}]);
                                        });
                                        return rows;
                                    }()
                                };
                            }
                        }, sortable: true, args: [{key: "chartData", data: args.chart.data}]
                    });
                    var getDataTable = function () {
                        return AJS.$("table.aui", gadget.getView());
                    };
                    var createCanvasDataTable = function () {
                        dataTable.addCallback(function (fragment) {
                            gadget.getView().append(fragment);
                            gadget.resize();
                        });
                        dataTable.build();
                    }; // finally after the image has loaded we create the data table
                    getChartImg().load(createCanvasDataTable);
                }
            }, args: [{
                key: "chart", ajaxOptions: function () {
                    var preferences = getPreferences(this);
                    var width = Math.round(gadgets.window.getViewportDimensions().width * 0.9); //canvas mode needs some more room to the right for the data table.
                    if (gadgets.views.getCurrentView().getName() === "canvas") {
                        width = Math.round(gadgets.window.getViewportDimensions().width * 0.6);
                    }
                    if (width < 150) {
                        width = 150;
                    }
                    var height = Math.round(width * 2 / 3);
                    if (gadgets.views.getCurrentView().getName() === "canvas") {
                        return {
                            url: "/rest/dianrong-gadget/1.0/planned-vs-released-chart/generate",
                            data: {
                                projectOrFilterId: gadgets.util.unescapeString(preferences.projectOrFilterId),
                                periodName: preferences.periodName,
                                daysprevious: preferences.daysprevious,
                                isCumulative: preferences.isCumulative,
                                showUnresolvedTrend: preferences.showUnresolvedTrend,
                                versionLabel: preferences.versionLabel,
                                width: Math.round(width * 0.8),
                                height: Math.round(height * 0.8),
                                returnData: true,
                                inline: true
                            }
                        };
                    } else {
                        return {
                            url: "/rest/dianrong-gadget/1.0/planned-vs-released-chart/generate",
                            data: {
                                projectOrFilterId: gadgets.util.unescapeString(preferences.projectOrFilterId),
                                periodName: preferences.periodName,
                                daysprevious: preferences.daysprevious,
                                isCumulative: preferences.isCumulative,
                                showUnresolvedTrend: preferences.showUnresolvedTrend,
                                versionLabel: preferences.versionLabel,
                                width: width,
                                height: height,
                                inline: true
                            }
                        };
                    }
                }
            }]
        }
    });


</script>
